import React, { useState, useEffect } from 'react';

// Pomodoro Timer component with task interleaving
function PomodoroTimer() {
  const [tasks, setTasks] = useState([]);                // List of tasks (subjects)
  const [currentTaskIndex, setCurrentTaskIndex] = useState(0); // Track current task index
  const [minutes, setMinutes] = useState(25);             // Default Pomodoro time: 25 minutes
  const [seconds, setSeconds] = useState(0);              // Start with 0 seconds
  const [isRunning, setIsRunning] = useState(false);      // Timer state (running or paused)
  const [isBreak, setIsBreak] = useState(false);          // Whether the timer is for a break or Pomodoro session

  // Add a new task to the task list
  const addTask = (taskName) => {
    setTasks((prevTasks) => [...prevTasks, taskName]);
  };

  // Switch to the next task in the list
  const switchTask = () => {
    setCurrentTaskIndex((prevIndex) => (prevIndex + 1) % tasks.length); // Loop through tasks
  };

  // Timer countdown effect
  useEffect(() => {
    let interval;

    // Start the timer if it's running
    if (isRunning) {
      interval = setInterval(() => {
        // Decrease time every second
        if (seconds === 0) {
          if (minutes === 0) {
            // Timer ends, switch to break or next task
            setIsBreak(!isBreak);
            setMinutes(isBreak ? 25 : 5);  // If it's a break, switch to 5 minutes; else, switch to 25 minutes
            setSeconds(0);

            if (isBreak && tasks.length > 0) {
              // Switch to the next task when break is over
              switchTask();
            }
          } else {
            setMinutes(minutes - 1);
            setSeconds(59);
          }
        } else {
          setSeconds(seconds - 1);
        }
      }, 1000); // Decrease every second
    } else {
      // If timer is paused, clear the interval
      clearInterval(interval);
    }

    return () => clearInterval(interval); // Cleanup interval on unmount
  }, [isRunning, minutes, seconds, isBreak, tasks]); // Re-run effect when timer state changes

  // Get current task name or default message if no tasks
  const currentTask = tasks[currentTaskIndex] || 'No task selected';

  return (
    <div className="timer">
      <h1>{isBreak ? "Break Time!" : `Pomodoro Timer - Task: ${currentTask}`}</h1>
      <div className="time">
        {String(minutes).padStart(2, '0')}:{String(seconds).padStart(2, '0')}
      </div>
      <button onClick={() => setIsRunning(!isRunning)}>
        {isRunning ? "Pause" : "Start"}
      </button>
      <button onClick={() => {
        setIsRunning(false); 
        setMinutes(25); 
        setSeconds(0);
        setIsBreak(false); // Reset the timer
      }}>
        Reset
      </button>

      {/* Task input form to add tasks */}
      <TaskManager addTask={addTask} />
    </div>
  );
}

// Component to manage adding tasks (subjects)
function TaskManager({ addTask }) {
  const [taskName, setTaskName] = useState('');

  // Handle adding a task
  const handleAddTask = () => {
    if (taskName.trim() === '') return;  // Don't add empty tasks
    addTask(taskName);                   // Add the task to the list
    setTaskName('');                     // Clear input after adding
  };

  return (
    <div className="task-manager">
      <h3>Add a Task</h3>
      <input 
        type="text" 
        value={taskName} 
        onChange={(e) => setTaskName(e.target.value)} 
        placeholder="Enter task name"
      />
      <button onClick={handleAddTask}>Add Task</button>
    </div>
  );
}

export default PomodoroTimer;
