import React, { useState } from "react";
import { BrowserRouter as Router, Route, Switch, useHistory } from "react-router-dom";
import './App.css';
import LoginForm from "./components/LoginForm";
import SignUpForm from "./components/SignUpForm";

function App() {
    const history = useHistory();

    const handleLoginSuccess = () => {
        // Redirect to the dashboard or main app after login
        window.location.href = "https://playful-pony-ea307f.netlify.app/"; // Redirect to your desired URL after login
    };

    const handleSignUpSuccess = () => {
        history.push("/login"); // Redirect to login page after successful sign-up
    };

    return (
        <Router>
            <div className="App">
                <h1>Study Buddy</h1>
                <Switch>
                    <Route exact path="/" component={SignUpForm} />
                    <Route exact path="/login">
                        <LoginForm onLoginSuccess={handleLoginSuccess} />
                    </Route>
                    <Route exact path="/signup">
                        <SignUpForm onSignUpSuccess={handleSignUpSuccess} />
                    </Route>
                </Switch>
            </div>
        </Router>
    );
}

export default App;
