// src/components/SignUpForm.js

import React, { useState } from 'react';
import axios from 'axios';
import { useHistory } from 'react-router-dom'; // To navigate after sign-up

function SignUpForm() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [message, setMessage] = useState('');
  const history = useHistory(); // Hook for navigating after successful signup

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.post('http://localhost:5000/api/signup', {
        email,
        password,
      });

      // On success, redirect to login page or display success message
      setMessage(`Sign-up successful!`);
      history.push('/');  // Redirect to login after successful sign-up
    } catch (error) {
      setMessage(error.response?.data?.msg || 'An error occurred during sign-up');
    }
  };

  return (
    <div>
      <h2>Sign Up</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Email:</label>
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Password:</label>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>
        <button type="submit">Sign Up</button>
      </form>
      {message && <p>{message}</p>}
    </div>
  );
}

export default SignUpForm;
